// üöÄ Ultrasonic Sensor Function
int getDistance() {
    digitalWrite(TRIG_PIN, LOW);
    delayMicroseconds(2);
    digitalWrite(TRIG_PIN, HIGH);
    delayMicroseconds(10);
    digitalWrite(TRIG_PIN, LOW);

    long duration = pulseIn(ECHO_PIN, HIGH, 30000); // 30ms timeout
    int distance = duration * 0.034 / 2; // Convert to cm

    if (distance == 0) distance = 1000; // If no obstacle detected, assume it's far

    Serial.print("Distance: ");
    Serial.println(distance);

    return distance;
}

void loop() {
    // üîÑ Update Ultrasonic Sensor Every 100ms (Non-blocking)
    if (millis() - lastUpdate >= 100) {
        lastUpdate = millis();
        int distance = getDistance();
    }

    // üîÑ Check for Bluetooth Command
    if (Serial.available() > 0) {
        String command = Serial.readStringUntil('\n'); // Read full command
        command.trim(); // Remove spaces and newlines
        Serial.println("Received: " + command); // Debugging

        Stop(); // Stop before executing new command

        // Execute Movement Based on Command
        int distance = getDistance();
        if (command == "F") forward(distance);
        else if (command == "B") back();
        else if (command == "L") left();
        else if (command == "R") right();
        else if (command == "I") ForwardRight();
        else if (command == "G") ForwardLeft();
        else if (command == "J") BackwardRight();
        else if (command == "H") BackwardLeft();
    }
}

