#define TRIG_PIN A0
#define ECHO_PIN A1
#define VIBRATION_SENSOR_PIN 13

void setupSensors() {
    pinMode(VIBRATION_SENSOR_PIN, INPUT);
    pinMode(TRIG_PIN, OUTPUT);
    pinMode(ECHO_PIN, INPUT);
}

int readVibrationSensor() {
    return digitalRead(VIBRATION_SENSOR_PIN);
}

int readUltrasonic() {
    digitalWrite(TRIG_PIN, LOW);
    delayMicroseconds(2);
    digitalWrite(TRIG_PIN, HIGH);
    delayMicroseconds(10);
    digitalWrite(TRIG_PIN, LOW);

    long duration = pulseIn(ECHO_PIN, HIGH, 20000); // Timeout at 20ms
    int distance = duration * 0.034 / 2;

    if (distance == 0) distance = 1000;  // No obstacle detected
    return distance;
}

