// Process Movement Based on Bluetooth Command
void processMotorCommand(char command, int distance) {
    if (distance < 20) {  
        digitalWrite(buz, HIGH); // Buzzer alert
        Stop();
        return;
    } else {
        digitalWrite(buz, LOW);
    }

    switch (command) {
        case 'F': forward(); break;
        case 'B': back(); break;
        case 'L': left(); break;
        case 'R': right(); break;
        case 'S': Stop(); break;
        default: break;
    }
}

