#include <AFMotor.h>

#define TRIG_PIN A0
#define ECHO_PIN A1
const int buz = 12;

// Motor Initialization
AF_DCMotor motor1(1, MOTOR12_1KHZ);
AF_DCMotor motor2(2, MOTOR12_1KHZ);
AF_DCMotor motor3(3, MOTOR34_1KHZ);
AF_DCMotor motor4(4, MOTOR34_1KHZ);

char command = ' ';  // Store Bluetooth command
unsigned long lastUpdate = 0;
int lastDistance = 100;  // Store last measured distance

void setup() {
    Serial.begin(9600);
    pinMode(TRIG_PIN, OUTPUT);
    pinMode(ECHO_PIN, INPUT);
    pinMode(buz, OUTPUT);
}

// üöÄ Optimized Distance Measurement
int getDistance() {
    digitalWrite(TRIG_PIN, LOW);
    delayMicroseconds(2);
    digitalWrite(TRIG_PIN, HIGH);
    delayMicroseconds(10);
    digitalWrite(TRIG_PIN, LOW);

    long duration = pulseIn(ECHO_PIN, HIGH, 20000);  // 20ms timeout
    int distance = duration * 0.034 / 2;

    if (distance == 0) distance = 1000;  // If no obstacle, assume far

    return distance;
}

// üöó Movement Functions
void forward() {
    if (lastDistance < 20) {
        digitalWrite(buz, HIGH);
        Stop();
    } else {
        digitalWrite(buz, LOW);
        motor1.setSpeed(250); motor1.run(FORWARD);
        motor2.setSpeed(250); motor2.run(FORWARD);
        motor3.setSpeed(250); motor3.run(FORWARD);
        motor4.setSpeed(250); motor4.run(FORWARD);
    }
}

void back() {
    motor1.setSpeed(250); motor1.run(BACKWARD);
    motor2.setSpeed(250); motor2.run(BACKWARD);
    motor3.setSpeed(250); motor3.run(BACKWARD);
    motor4.setSpeed(250); motor4.run(BACKWARD);
}

void left() {
    motor1.setSpeed(250); motor1.run(BACKWARD);
    motor2.setSpeed(250); motor2.run(BACKWARD);
    motor3.setSpeed(250); motor3.run(FORWARD);
    motor4.setSpeed(250); motor4.run(FORWARD);
}

void right() {
    motor1.setSpeed(250); motor1.run(FORWARD);
    motor2.setSpeed(250); motor2.run(FORWARD);
    motor3.setSpeed(250); motor3.run(BACKWARD);
    motor4.setSpeed(250); motor4.run(BACKWARD);
}

void Stop() {
    motor1.run(RELEASE);
    motor2.run(RELEASE);
    motor3.run(RELEASE);
    motor4.run(RELEASE);
}

void loop() {
    // üîÑ Update Ultrasonic Sensor Every 50ms (Faster Response)
    if (millis() - lastUpdate >= 50) {
        lastUpdate = millis();
        lastDistance = getDistance();
    }

    // üîÑ Read Bluetooth Command Instantly
    if (Serial.available() > 0) {
        command = Serial.read();
        Serial.print("Received: ");
        Serial.println(command);
    }

    // üîÑ Execute Movement Instantly
    switch (command) {
        case 'F': forward(); break;
        case 'B': back(); break;
        case 'L': left(); break;
        case 'R': right(); break;
        case 'S': Stop(); break;
        default: break;
    }
}

